import React, { useState } from 'react';
import { createGroup } from '../../service/api'; // Ensure createGroup API is implemented // Add styles as per your requirement

const CreateGroup = () => {
  const [groupName, setGroupName] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!groupName) {
      setError('Group Name is required');
      return;
    }
    if (!trainerId) {
        setError('Trainer ID not found in localStorage.');
        return;
      }
    const groupData = {
      name: groupName,
      trainer: localStorage.getItem('trainerId'), // Get trainer ID from localStorage or context
    };
  console.log(groupData)
    try {
      const response = await createGroup(groupData);
      alert('Group Created Successfully!');
      console.log(response)
      setGroupName('');
    } catch (error) {
      console.error('Error creating group:', error);
      setError('Failed to create group');
    }
  };

  return (
    <div className="create-group-container">
      <h2>Create New Group</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {error && <div className="error">{error}</div>}
        <input
          type="text"
          placeholder="Group Name"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
          required
          className="border p-2 w-full"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Create Group
        </button>
      </form>
    </div>
  );
};

export default CreateGroup;
