import React, { useState } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import '../styles/GoogleSignup.css';

const GoogleSignup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (response) => {
    try {
      const res = await axios.post('http://localhost:5000/api/auth/google-login', { tokenId: response.tokenId });
      console.log(res.data.user);
      // Save token and user info in local storage or context
      redirectUser(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleEmailLogin = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', { email, password});
      console.log(res.data.user);
      localStorage.setItem('token', res.data.token);
       localStorage.setItem('trainerId', res.data.user._id);
      redirectUser(res.data.user.role);
    } catch (error) {
      console.error(error);
    }
  };

  const redirectUser = (role) => {
    const normalizedRole = role.toLowerCase();
    if (normalizedRole === 'trainer') {
      window.location.href = '/dashboard/trainer';
    } else if (normalizedRole === 'trainee') {
      window.location.href = '/dashboard/trainee';
    } else {
      window.location.href = '/';
    }
  };

  return (
    <div className="google-signup-container">
      <div className="email-login-container">
        <h1>Login</h1>
        <div className="input-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button onClick={handleEmailLogin}>Login</button>
        <h4>or</h4>
        <GoogleLogin
          clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}
          buttonText="Signup with Google"
          onSuccess={handleLogin}
          onFailure={(error) => console.error(error)}
          cookiePolicy={'single_host_origin'}
        />
      </div>
    </div>
  );
};

export default GoogleSignup;
