import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from '../service/api'; // adjust this to your axios base config

const RegisterInvite = () => {
  const { token } = useParams();
  const [inviteData, setInviteData] = useState(null);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({ name: '', password: '' });

  useEffect(() => {
    const fetchInvite = async () => {
      try {
        const res = await axios.get(`/api/invite/${token}`);
        setInviteData(res.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Invalid or expired token');
      }
    };
    fetchInvite();
  }, [token]);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/auth/register-invite', {
        token,
        ...formData,
      });
      alert('Registration successful!');
    } catch (err) {
      alert(err.response?.data?.message || 'Registration failed');
    }
  };

  if (error) {
    return <div className="text-red-500 p-4 text-center">{error}</div>;
  }

  if (!inviteData) {
    return <div className="text-gray-500 p-4 text-center">Validating invite...</div>;
  }

  return (
    <div className="max-w-md mx-auto mt-10 bg-white shadow-md rounded p-6">
      <h2 className="text-xl font-bold mb-4">Register for Fitness Tracker</h2>
      <p className="mb-2 text-sm">Invited as: <strong>{inviteData.email}</strong></p>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          required
          className="w-full border px-3 py-2 rounded"
        />
        <input
          type="password"
          name="password"
          placeholder="Create Password"
          value={formData.password}
          onChange={handleChange}
          required
          className="w-full border px-3 py-2 rounded"
        />
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Register
        </button>
      </form>
    </div>
  );
};

export default RegisterInvite;
