import React, { useState } from 'react';
import axios from 'axios';
import { Bar, Line, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement,
  LineElement, BarElement, ArcElement, Tooltip, Legend
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Tooltip, Legend);

const chartTypes = [Bar, Line, Pie];

const TrainerProgress = () => {
  const [filters, setFilters] = useState({
    date: '', month: '', year: '', city: '', country: ''
  });
  const [groupedData, setGroupedData] = useState({});

  const handleChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const fetchData = async () => {
    const query = new URLSearchParams(filters).toString();
    try {
      const res = await axios.get(`http://localhost:5000/api/progress/trainer/filter?${query}`);
      const data = res.data;

      const grouped = {};
      data.forEach((act) => {
        if (!grouped[act.activityType]) grouped[act.activityType] = [];
        grouped[act.activityType].push(act);
      });

      setGroupedData(grouped);
    } catch (err) {
      console.error('Error fetching trainer data:', err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Trainer - Filtered Activity Progress</h2>

      <form className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-6" onSubmit={(e) => { e.preventDefault(); fetchData(); }}>
        <input type="date" name="date" value={filters.date} onChange={handleChange} className="border p-2 rounded" />
        <select name="month" onChange={handleChange} className="border p-2 rounded">
          <option value="">Month</option>
          {[...Array(12)].map((_, i) => (
            <option key={i} value={`${(i + 1).toString().padStart(2, '0')}`}>{i + 1}</option>
          ))}
        </select>
        <select name="year" onChange={handleChange} className="border p-2 rounded">
          <option value="">Year</option>
          {[2023, 2024, 2025].map(y => <option key={y} value={y}>{y}</option>)}
        </select>
        <input type="text" name="city" placeholder="City" onChange={handleChange} className="border p-2 rounded" />
        <input type="text" name="country" placeholder="Country" onChange={handleChange} className="border p-2 rounded" />
        <button type="submit" className="bg-blue-600 text-white rounded px-4 py-2 col-span-2 md:col-span-3">Apply Filters</button>
      </form>

      <div className="grid md:grid-cols-2 gap-6">
        {Object.entries(groupedData).map(([type, records], index) => {
          const ChartComponent = chartTypes[index % chartTypes.length];
          const chartData = {
            labels: records.map(r => new Date(r.date).toLocaleDateString()),
            datasets: [{
              label: `${type} Duration`,
              data: records.map(r => r.duration),
              backgroundColor: ['#f67019', '#4dc9f6', '#f53794', '#537bc4'],
              borderColor: '#333',
              fill: false,
              tension: 0.3
            }]
          };

          return (
            <div key={type} className="bg-white p-4 shadow rounded">
              <h3 className="text-lg font-semibold mb-2">{type}</h3>
              <ChartComponent data={chartData} />
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default TrainerProgress;
