import React, { useState, useEffect } from 'react';
import { createGroup, getTrainees } from '../../service/api'; // Ensure getTrainees API is implemented

const CreateGroup = () => {
  const [groupName, setGroupName] = useState('');
  const [selectedTrainees, setSelectedTrainees] = useState([]);
  const [trainees, setTrainees] = useState([]);
  const [error, setError] = useState('');

  // Fetch the list of trainees when the component is mounted
  useEffect(() => {
    const fetchTrainees = async () => {
      try {
        const response = await getTrainees();
        setTrainees(response.data);
      } catch (error) {
        console.error('Error fetching trainees:', error);
        setError('Failed to fetch trainees');
      }
    };
    fetchTrainees();
  }, []);

  // Handle the submission of the group creation
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!groupName) {
      setError('Group Name is required');
      return;
    }

    const groupData = {
      name: groupName,
      trainer: localStorage.getItem('trainerId'), // Get trainer ID from localStorage or context
      trainees: selectedTrainees, // Include selected trainees
    };

    try {
      const response = await createGroup(groupData);
      alert('Group Created Successfully!');
      console.log(response);
      setGroupName('');
      setSelectedTrainees([]);
    } catch (error) {
      console.error('Error creating group:', error);
      setError('Failed to create group');
    }
  };

  // Handle trainee selection
  const handleTraineeChange = (e) => {
    const { value, checked } = e.target;
    setSelectedTrainees(prevState =>
      checked ? [...prevState, value] : prevState.filter(traineeId => traineeId !== value)
    );
  };

  return (
    <div className="create-group-container">
      <h2>Create New Group</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {error && <div className="error">{error}</div>}
        
        <input
          type="text"
          placeholder="Group Name"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
          required
          className="border p-2 w-full"
        />
        
        <div className="trainee-selection">
          <h3>Select Trainees</h3>
          {trainees.length > 0 ? (
            trainees.map((trainee) => (
              <div key={trainee._id}>
                <label>
                  <input
                    type="checkbox"
                    value={trainee._id}
                    onChange={handleTraineeChange}
                  />
                  {trainee.name}
                </label>
              </div>
            ))
          ) : (
            <p>No trainees available</p>
          )}
        </div>
        
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Create Group
        </button>
      </form>
    </div>
  );
};

export default CreateGroup;
