// src/components/trainer/CreatePlan.js

import React, { useState } from 'react';
import axios from 'axios';
const CreatePlan = () => {
   const [type, setType] = useState('');
   const [duration, setDuration] = useState('');
    const handleCreate = () => {
          axios.post('/api/trainer/plan', { type, duration })
            .then(() => alert('Plan created'))
            .catch(err => console.error(err));
        };
      
    return (
          <div className="p-4">
            <h2 className="text-xl font-semibold mb-3">Create Plan</h2>
            <select className="border p-2 w-full mb-2" value={type} onChange={(e) => setType(e.target.value)}>
              <option value="">Select Type</option>
              <option value="Cardio">Cardio</option>
              <option value="Strength">Strength</option>
            </select>
            <input className="border p-2 w-full mb-2" placeholder="Duration (min)" type="number" value={duration} onChange={(e) => setDuration(e.target.value)} />
            <button className="bg-green-600 text-white px-4 py-2 rounded" onClick={handleCreate}>Create</button>
          </div>
        );      
}

export default CreatePlan;
