// components/trainee/traineePlan.js
import React, { useEffect, useState } from 'react';
import {getAssignedPlan} from '../../service/api'; // Correct path

const TraineePlan = () => {
  const [plans, setPlans] = useState([]);

  useEffect(() => {
    const fetchAssignedPlans = async () => {
      try {
        const userId=localStorage.getItem('userId');
        if (!userId) {
          console.error('User not logged in!');
          return;
        } // fetch trainee id
        const response = await getAssignedPlan(userId)
        setPlans(response);
      } catch (error) {
        console.error('Error fetching assigned plans:', error);
      }
    };

    fetchAssignedPlans();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">My Assigned Workout Plans</h2>
      {plans.length === 0 ? (
        <p>No plans assigned yet.</p>
      ) : (
        plans.map(plan => (
          <div key={plan._id} className="mb-4 p-4 border rounded shadow">
            <h3 className="text-xl font-semibold">{plan.planName}</h3>
            <p><strong>Exercise Type:</strong> {plan.exerciseType}</p>
            <p><strong>Workout Type:</strong> {plan.workoutType}</p>
            <p><strong>Duration:</strong> {plan.duration} minutes</p>
          </div>
        ))
      )}
    </div>
  );
};

export default TraineePlan;
