import React, { useState, useEffect } from 'react';
import { createGroup, getGroupsByTrainer, editGroupName, getTraineesInGroup } from '../../service/api';

const ManageGroups = () => {
  const [groups, setGroups] = useState([]);
  const [newGroupName, setNewGroupName] = useState('');
  const [editMode, setEditMode] = useState({});
  const [trainerId, setTrainerId] = useState('');

  useEffect(() => {
    const id = localStorage.getItem('trainerId');
    setTrainerId(id);
    if (id) {
      loadGroups(id);
    }
  }, []);

  const loadGroups = async (id) => {
    try {
      const res = await getGroupsByTrainer(id);
      setGroups(res.data);
    } catch (error) {
      console.error('Error loading groups:', error);
    }
  };

  const handleCreateGroup = async () => {
    try {
      await createGroup({ name: newGroupName, trainer: trainerId });
      setNewGroupName('');
      loadGroups(trainerId);
    } catch (error) {
      console.error('Error creating group:', error);
    }
  };

  const handleEditGroup = async (groupId, newName) => {
    try {
      await editGroupName(groupId, { name: newName });
      loadGroups(trainerId);
    } catch (error) {
      console.error('Error editing group:', error);
    }
  };

  const handleViewTrainees = async (groupId) => {
    try {
      const res = await getTraineesInGroup(groupId);
      alert(`Trainees:\n${res.data.map(t => t.name).join('\n')}`);
    } catch (error) {
      console.error('Error fetching trainees:', error);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Manage Groups</h2>

      <div className="flex mb-4 gap-2">
        <input
          type="text"
          value={newGroupName}
          onChange={(e) => setNewGroupName(e.target.value)}
          placeholder="New Group Name"
          className="border p-2 flex-grow"
        />
        <button onClick={handleCreateGroup} className="bg-green-500 text-white px-4 py-2 rounded">
          Create
        </button>
      </div>

      <div>
        {groups.map(group => (
          <div key={group._id} className="border p-4 mb-2 flex justify-between items-center">
            {editMode[group._id] ? (
              <input
                type="text"
                defaultValue={group.name}
                onBlur={(e) => {
                  handleEditGroup(group._id, e.target.value);
                  setEditMode({ ...editMode, [group._id]: false });
                }}
                className="border p-2"
              />
            ) : (
              <h3 className="font-semibold">{group.name}</h3>
            )}
            <div className="flex gap-2">
              <button onClick={() => setEditMode({ ...editMode, [group._id]: true })} className="bg-yellow-500 text-white px-2 py-1 rounded">
                Edit
              </button>
              <button onClick={() => handleViewTrainees(group._id)} className="bg-blue-500 text-white px-2 py-1 rounded">
                View Trainees
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default viewGroups;
