import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { verifyInvite } from '../../service/api'; // API to verify invite

const Register = () => {
  const { token } = useParams(); // Extract token from URL
  const [invite, setInvite] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchInvite = async () => {
      try {
        const inviteData = await verifyInvite(token);
        setInvite(inviteData);
      } catch (error) {
        setError('Invalid or expired token');
      } finally {
        setLoading(false);
      }
    };

    fetchInvite();
  }, [token]);

  const handleAccept = async () => {
    try {
      // Logic to accept the invite and join the group
      alert('You have successfully joined the group!');
    } catch (error) {
      setError('Failed to join the group');
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Join Group</h2>
      {invite && (
        <div>
          <p>You have been invited to join the group: {invite.group.name}</p>
          <button onClick={handleAccept}>Accept Invitation</button>
        </div>
      )}
    </div>
  );
};

export default Register;
