import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api',
});

export const signup = async (formData) => {
  const response = await api.post('/users/signup', formData);
  return response.data;
};

export const googleSignup = async (tokenId) => {
  const response = await api.post('/google-login', { tokenId });
  return response.data;
};

export const inviteTrainee = async (inviteData) => {
  const response = await api.post('/invite', inviteData);
  return response.data;
};

export const createGroup = (data) => axios.post('/api/groups', data);

export const getGroupsByTrainer = (trainerId) => axios.get(`/api/groups/trainer/${trainerId}`);

export const editGroupName = (groupId, data) => axios.put(`/api/groups/${groupId}`, data);

export const getTraineesInGroup = (groupId) => axios.get(`/api/groups/${groupId}/trainees`);

export const getAssignedPlan = async (traineeId) => {
  const response = await api.get(`/api/traineePlan/${traineeId}`);
  return response.data;
};
export default api;
