import React, { useState, useEffect } from 'react';
import '../../styles/createPlan.css';
import axios from 'axios';

const CreatePlan = () => {
  const [formData, setFormData] = useState({
    planName: '',
    exerciseType: '',
    duration: '',
    workoutType: '',
    traineeEmail: '' // Trainee email input for assignment
  });

  const [trainees, setTrainees] = useState([]); // List of trainees to assign the plan
  const [message, setMessage] = useState(''); // Success/Error message

  useEffect(() => {
    // Fetch list of trainees from the backend (or you can hard-code them for now)
    axios.get('http://localhost:5000/api/trainees')
      .then((response) => {
        setTrainees(response.data); // Assuming the response is a list of trainees with email
      })
      .catch((error) => {
        console.error('Error fetching trainees:', error);
      });
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send plan data and trainee email to backend
      const response = await axios.post('http://localhost:5000/api/plans', formData);
      setMessage('Plan created and assigned successfully!');
      console.log('Plan created successfully:', response.data);
      setFormData({
        planName: '',
        exerciseType: '',
        duration: '',
        workoutType: '',
        traineeEmail: ''
      });
    } catch (error) {
      setMessage('Error creating plan. Please try again!');
      console.error('Error creating plan:', error);
    }
  };

  return (
    <div className="form-container">
      <h2>Create Training Plan</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="planName">Plan Name</label>
          <input
            type="text"
            name="planName"
            placeholder="Enter plan name"
            value={formData.planName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="exerciseType">Exercise Type</label>
          <input
            type="text"
            name="exerciseType"
            placeholder="Enter exercise type"
            value={formData.exerciseType}
            onChange={handleChange}
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="duration">Duration (minutes)</label>
          <input
            type="number"
            name="duration"
            placeholder="Enter duration"
            value={formData.duration}
            onChange={handleChange}
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="workoutType">Workout Type</label>
          <input
            type="text"
            name="workoutType"
            placeholder="Enter workout type"
            value={formData.workoutType}
            onChange={handleChange}
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="traineeEmail">Assign to Trainee (Email)</label>
          <select
            name="traineeEmail"
            value={formData.traineeEmail}
            onChange={handleChange}
            required
          >
            <option value="">Select Trainee</option>
            {trainees.map((trainee) => (
              <option key={trainee._id} value={trainee.email}>
                {trainee.name} ({trainee.email})
              </option>
            ))}
          </select>
        </div>

        <button type="submit" className="submit-btn">Create Plan</button>
      </form>

      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default CreatePlan;
