import React, { useState } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import './Login.css';

const CLIENT_ID = 'YOUR_GOOGLE_CLIENT_ID_HERE'; // Replace with actual client ID

function Login({ setUser }) {
  const [form, setForm] = useState({ email: '', password: '' });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleLogin = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/email-login', form);
      setUser(res.data);
      alert('Logged in via email/password');
    } catch (err) {
      console.error(err);
      alert('Invalid email or password');
    }
  };

  const handleGoogleSuccess = async (response) => {
    try {
      const res = await axios.post('http://localhost:5000/api/google-login', {
        token: response.credential,
      });
      setUser(res.data);
      alert('Logged in with Google');
    } catch (error) {
      console.error('Google login error', error);
      alert('Google login failed');
    }
  };

  const handleGoogleFailure = (response) => {
    console.error('Google Sign In was unsuccessful. Try again later.', response);
  };

  return (
    <div className="login-container">
      <h2>Login</h2>

      <form onSubmit={handleLogin} className="login-form">
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          required
        />

        <button type="submit">Login</button>
      </form>

      <div className="divider">OR</div>

      <GoogleOAuthProvider clientId={CLIENT_ID}>
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onError={handleGoogleFailure}
        />
      </GoogleOAuthProvider>
    </div>
  );
}

export default Login;
