import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {Line, Bar, Pie} from 'react-chartjs-2';
import {Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Tooltip, Legend} from 'chart.js';

ChartJS.register(
  CategoryScale, LinearScale, PointElement, LineElement, BarElement,
  ArcElement, Tooltip, Legend
);

const ProgressCharts = () => {
  const [data, setData] = useState({ running: [], cycling: [], swimming: [] });
  const traineeId = localStorage.getItem('userId');

  useEffect(() => {
    if (!traineeId) {
        console.warn("No trainee ID found in localStorage");
        return;
      }
    const fetchProgress = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/progress/${traineeId}/progress`);
        setData(res.data);
      } catch (err) {
        console.error('Error fetching progress:', err);
      }
    };

    fetchProgress();
  }, [traineeId]);

  // Format data for chart.js
  const lineData = {
    labels: data.running.map(d => d.date),
    datasets: [{
      label: 'Running Progress',
      data: data.running.map(d => d.duration),
      fill: false,
      borderColor: 'blue',
      tension: 0.1
    }]
  };

  const barData = {
    labels: data.cycling.map(d => d.date),
    datasets: [{
      label: 'Cycling Progress',
      data: data.cycling.map(d => d.duration),
      backgroundColor: 'orange'
    }]
  };

  const pieData = {
    labels: data.swimming.map(d => d.date),
    datasets: [{
      label: 'Swimming Progress',
      data: data.swimming.map(d => d.duration),
      backgroundColor: ['#4dc9f6', '#f67019', '#f53794', '#537bc4']
    }]
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 p-6">
      <div className="bg-white p-4 shadow rounded">
        <h3 className="text-lg font-semibold mb-2">Running (Line)</h3>
        <Line data={lineData} />
      </div>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="text-lg font-semibold mb-2">Cycling (Bar)</h3>
        <Bar data={barData} />
      </div>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="text-lg font-semibold mb-2">Swimming (Pie)</h3>
        <Pie data={pieData} />
      </div>
    </div>
  );
};

export default ProgressCharts;
