import React, { useState } from 'react';
import axios from 'axios';

const CreatePlan = () => {
  const [plan, setPlan] = useState({
    exercise_type: '',
    duration: '',
    workout_type: '',
    trainee_email: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPlan({
      ...plan,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/create-plan', plan);
      alert('Plan created successfully');
    } catch (error) {
      console.error('Error creating plan', error);
    }
  };

  return (
    <div>
      <h2>Create Workout Plan</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="exercise_type"
          value={plan.exercise_type}
          onChange={handleInputChange}
          placeholder="Exercise Type"
          required
        />
        <input
          type="number"
          name="duration"
          value={plan.duration}
          onChange={handleInputChange}
          placeholder="Duration (minutes)"
          required
        />
        <input
          type="text"
          name="workout_type"
          value={plan.workout_type}
          onChange={handleInputChange}
          placeholder="Type of Workout"
          required
        />
        <input
          type="email"
          name="trainee_email"
          value={plan.trainee_email}
          onChange={handleInputChange}
          placeholder="Trainee Email"
          required
        />
        <button type="submit">Create Plan</button>
      </form>
    </div>
  );
};

export default CreatePlan;
