import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
} from 'chart.js';
import '../../styles/progress.css';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

const ProgressCharts = () => {
  const [progressData, setProgressData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const traineeId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchData = async () => {
      if (!traineeId) {
        setError('User ID not found.');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(`http://localhost:5000/api/progress/${traineeId}/progress`);
        setProgressData(res.data || {});
      } catch (err) {
        console.error(err);
        setError('Failed to load progress data.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [traineeId]);

  if (loading) return <p className="p-4">Loading...</p>;
  if (error) return <p className="p-4 text-red-500">{error}</p>;

  const renderCharts = () => {
    return Object.entries(progressData).map(([activityType, entries]) => {
      const chartData = {
        labels: entries.map(e => e.date),
        datasets: [{
          label: `${activityType.charAt(0).toUpperCase() + activityType.slice(1)} Progress`,
          data: entries.map(e => e.duration),
          borderColor: 'teal',
          backgroundColor: 'rgba(0, 128, 128, 0.2)',
          fill: true,
          tension: 0.2
        }]
      };

      return (
        <div key={activityType} className="chart-card">
          <h3>{activityType.charAt(0).toUpperCase() + activityType.slice(1)}</h3>
          <Line data={chartData} />
        </div>
      );
    });
  };

  return (
    <div className="progress-container">
      <div className="chart-grid">
        {renderCharts()}
      </div>
    </div>
  );
};

export default ProgressCharts;
