import React, { useState } from 'react';
import CreatePlan from './createPlan'; // Make sure the import path is correct

const TrainerDashboard = () => {
  const [showCreatePlan, setShowCreatePlan] = useState(false);

  const handleCreatePlanClick = () => {
    setShowCreatePlan(!showCreatePlan);
  };

  return (
    <div>
      <h2>Trainer Dashboard</h2>
      <button onClick={handleCreatePlanClick}>
        {showCreatePlan ? 'Hide Plan Form' : 'Create Plan'}
      </button>

      {/* Conditionally render the CreatePlan component */}
      {showCreatePlan && <CreatePlan />}
    </div>
  );
};

export default TrainerDashboard;
