import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewPlan = () => {
  const [plan, setPlan] = useState(null);

  useEffect(() => {
    // Replace with the trainee's email or user identifier
    axios
      .get('http://localhost:5000/api/get-plans/trainee@example.com')
      .then((response) => {
        setPlan(response.data);
      });
  }, []);

  if (!plan) return <p>Loading...</p>;

  return (
    <div>
      <h3>Your Workout Plan</h3>
      <ul>
        {plan.map((p, index) => (
          <li key={index}>
            <h4>{p.exercise_type}</h4>
            <p>Duration: {p.duration} minutes</p>
            <p>Workout Type: {p.workout_type}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ViewPlan;
