import React, { useState, useEffect } from 'react';
import { inviteTrainee, getGroups } from '../../service/api';  // Import your API service
import '../../styles/InviteTrainee.css';

const InviteTrainee = () => {
  const [email, setEmail] = useState('');
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState('');

  // Fetch available groups from the server
  useEffect(() => {
    const fetchGroups = async () => {
      try {
        const response = await getGroups(); // Make sure this function fetches groups
        setGroups(response.data);
      } catch (error) {
        console.error('Error fetching groups:', error);
      }
    };

    fetchGroups();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const trainerId = localStorage.getItem('trainerId'); // Assuming trainer ID is saved in localStorage
    if (!trainerId) {
      alert('Trainer ID is missing!');
      return;
    }

    try {
      const inviteData = {
        email,
        trainer: trainerId,
        group: selectedGroup,
      };

      // Sending the invite to the backend
      await inviteTrainee(inviteData);

      alert('Invite sent successfully!');
      setEmail('');
      setSelectedGroup('');
    } catch (error) {
      console.error('Error inviting trainee:', error);
      alert('Failed to send invite');
    }
  };

  return (
    <div className="invite-container">
      <h2>Invite a Trainee</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Trainee Email"
          required
          className="border p-2 w-full"
        />
        
        <select
          value={selectedGroup}
          onChange={(e) => setSelectedGroup(e.target.value)}
          required
          className="border p-2 w-full"
        >
          <option value="">Select a group</option>
          {groups.map((group) => (
            <option key={group._id} value={group._id}>
              {group.name}
            </option>
          ))}
        </select>

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Send Invite
        </button>
      </form>
    </div>
  );
};

export default InviteTrainee;
