import React, { useState, useEffect } from 'react';
import { inviteTrainee, getGroups } from '../../service/api';  // ✅ import getGroups
import '../../styles/InviteTrainee.css'
const InviteTrainee = () => {
  const [email, setEmail] = useState('');
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState('');

  useEffect(() => {
    const fetchGroups = async () => {
      try {
        const groupList = await getGroups();
        setGroups(groupList);
      } catch (error) {
        console.error('Error fetching groups:', error);
      }
    };

    fetchGroups();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const invitedBy = localStorage.getItem('trainerId'); // trainerId from localstorage

      await inviteTrainee({ 
        email, 
        trainer:invitedBy, 
        group: selectedGroup  // ✅ send selected groupId too
      });

      alert('Invite sent successfully!');
      setEmail('');
      setSelectedGroup('');
    } catch (error) {
      console.error(error);
      alert('Failed to send invite');
    }
  };

  return (
    <div className="p-4">
      <h2>Invite a Trainee</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Trainee Email"
          required
          className="border p-2 w-full"
        />

        <select
          value={selectedGroup}
          onChange={(e) => setSelectedGroup(e.target.value)}
          required
          className="border p-2 w-full"
        >
          <option value="">Select a group</option>
          {groups.map((group,index) => (
            <option key={group._id||index} value={group._id}>
              {group.name}
            </option>
          ))}
        </select>

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Send Invite
        </button>
      </form>
    </div>
  );
};

export default InviteTrainee;
