import React, { useState, useEffect } from 'react';
import CreatePlan from '../trainer/createPlan';
import InviteTrainee from '../trainer/InviteTrainee';
import CreateGroup from '../trainer/createGroup';
import ViewGroup from '../trainer/viewGroup';
import axios from 'axios';
import '../../styles/trainerDashboard.css';

const TrainerDashboard = () => {
  const [activeComponent, setActiveComponent] = useState('');
  const [groups, setGroups] = useState([]);
  const [selectedGroupId, setSelectedGroupId] = useState(null);

  const handleButtonClick = (componentName) => {
    setActiveComponent(componentName);
  };

  useEffect(() => {
    if (activeComponent === 'ViewGroup') {
      fetchGroups();
    }
  }, [activeComponent]);

  const fetchGroups = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/group/groups');
      setGroups(res.data);
    } catch (err) {
      console.error('Error fetching groups', err);
    }
  };

  const handleGroupClick = (groupId) => {
    setSelectedGroupId(groupId);
  };

  return (
    <div>
      <div className="navbar">
        <h2>Trainer Dashboard</h2>
        <button onClick={() => handleButtonClick('CreatePlan')}>Create Plan</button>
        <button onClick={() => handleButtonClick('InviteTrainee')}>Invite Trainee</button>
        <button onClick={() => handleButtonClick('CreateGroup')}>Create Groups</button>
        <button onClick={() => handleButtonClick('ViewGroup')}>View Groups</button>
      </div>

      <div className="content">
        {activeComponent === 'CreatePlan' && <CreatePlan />}
        {activeComponent === 'InviteTrainee' && <InviteTrainee />}
        {activeComponent === 'CreateGroup' && <CreateGroup />}

        {activeComponent === 'ViewGroup' && !selectedGroupId && (
          <div>
            <h3>All Groups:</h3>
            <ul>
              {groups.map((group) => (
                <li key={group._id}>
                  <button onClick={() => handleGroupClick(group._id)}>
                    {group.name}
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}

        {selectedGroupId && <ViewGroup groupId={selectedGroupId} />}
      </div>
    </div>
  );
};

export default TrainerDashboard;
