import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line, Bar, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

const ProgressCharts = () => {
  const [data, setData] = useState({ jumpingjacks: [], cycling: [], swimming: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const traineeId = localStorage.getItem('userId');

  useEffect(() => {
    if (!traineeId) {
      console.warn("No trainee ID found in localStorage");
      setError('User ID not found.');
      setLoading(false);
      return;
    }

    const fetchProgress = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/progress/${traineeId}/progress`);
        const resData = res.data || {};
        setData({
          jumpingjacks: resData.jumpingjacks || [],
          cycling: resData.cycling || [],
          swimming: resData.swimming || []
        });
      } catch (err) {
        console.error('Error fetching progress:', err);
        setError('Failed to load progress data.');
      } finally {
        setLoading(false);
      }
    };

    fetchProgress();
  }, [traineeId]);

  const lineData = {
    labels: (data.jumpingjacks || []).map(d => d.date),
    datasets: [{
      label: 'Jumping Jacks Progress',
      data: (data.jumpingjacks || []).map(d => d.duration),
      fill: false,
      borderColor: 'blue',
      tension: 0.1
    }]
  };

  const barData = {
    labels: (data.cycling || []).map(d => d.date),
    datasets: [{
      label: 'Cycling Progress',
      data: (data.cycling || []).map(d => d.duration),
      backgroundColor: 'orange'
    }]
  };

  const pieData = {
    labels: (data.swimming || []).map(d => d.date),
    datasets: [{
      label: 'Swimming Progress',
      data: (data.swimming || []).map(d => d.duration),
      backgroundColor: ['#4dc9f6', '#f67019', '#f53794', '#537bc4']
    }]
  };

  if (loading) return <p className="p-4">Loading charts...</p>;
  if (error) return <p className="p-4 text-red-500">{error}</p>;

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 p-6">
      <div className="bg-white p-4 shadow rounded">
        <h3 className="text-lg font-semibold mb-2">Jumping Jacks</h3>
        {data.jumpingjacks.length > 0 ? <Line data={lineData} /> : <p>No data available</p>}
      </div>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="text-lg font-semibold mb-2">Cycling</h3>
        {data.cycling.length > 0 ? <Bar data={barData} /> : <p>No data available</p>}
      </div>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="text-lg font-semibold mb-2">Swimming</h3>
        {data.swimming.length > 0 ? <Pie data={pieData} /> : <p>No data available</p>}
      </div>
    </div>
  );
};

export default ProgressCharts;
