import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = ({ user }) => {
  const [plans, setPlans] = useState([]);

  useEffect(() => {
    if (user) {
      // Fetch plans or activities based on user role
      if (user.role === 'trainer') {
        // Fetch all plans for trainer
        axios.get('http://localhost:5000/api/get-plans').then((response) => {
          setPlans(response.data);
        });
      } else {
        // Fetch the specific trainee plan
        axios
          .get(`http://localhost:5000/api/get-plans/${user.email}`)
          .then((response) => {
            setPlans(response.data);
          });
      }
    }
  }, [user]);

  return (
    <div>
      <h2>Welcome, {user ? user.name : 'User'}</h2>
      <h3>Your Plans</h3>
      {plans.length > 0 ? (
        <ul>
          {plans.map((plan, index) => (
            <li key={index}>
              <h4>{plan.exercise_type}</h4>
              <p>Duration: {plan.duration}</p>
              <p>Type of workout: {plan.workout_type}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No plans available.</p>
      )}
    </div>
  );
};

export default Dashboard;
