import React, { useState } from 'react';
import axios from 'axios';
import '../../styles/createPlan.css'
const CreatePlan = () => {
  const [planData, setPlanData] = useState({
    traineeId: '',
    type: '',
    duration: '',
    workoutType: '',
  });

  const handleChange = (e) => {
    setPlanData({ ...planData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/plans/create', planData);
      alert('Training plan created successfully');
    } catch (error) {
      console.error('Error creating plan:', error);
    }
  };

  return (
    <div>
      <h2>Create Training Plan</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="traineeId">Trainee ID:</label>
        <input
          type="text"
          name="traineeId"
          value={planData.traineeId}
          onChange={handleChange}
          required
        />
        <label htmlFor="type">Type of Exercise:</label>
        <input
          type="text"
          name="type"
          value={planData.type}
          onChange={handleChange}
          required
        />
        <label htmlFor="duration">Duration:</label>
        <input
          type="text"
          name="duration"
          value={planData.duration}
          onChange={handleChange}
          required
        />
        <label htmlFor="workoutType">Workout Type:</label>
        <input
          type="text"
          name="workoutType"
          value={planData.workoutType}
          onChange={handleChange}
          required
        />
        <button type="submit">Create Plan</button>
      </form>
    </div>
  );
};

export default CreatePlan;
