import React, { useState } from 'react';
import CreatePlan from '../trainer/createPlan';
import InviteTrainee from '../trainer/InviteTrainee';
import CreateGroup from '../trainer/createGroup';
import ViewProgress from '../trainer/ProgressDashboard';
import '../../styles/trainerDashboard.css'
const TrainerDashboard = () => {
  const [activeComponent, setActiveComponent] = useState('');

  const handleButtonClick = (componentName) => {
    setActiveComponent(componentName);
  };

  return (
    <div>
      <div className="navbar">
        <h2>Trainer Dashboard</h2>
        <button onClick={() => handleButtonClick('CreatePlan')}>Create Plan</button>
        <button onClick={() => handleButtonClick('InviteTrainee')}>Invite Trainee</button>
        <button onClick={() => handleButtonClick('CreateGroup')}>Create Groups</button>
        <button onClick={() => handleButtonClick('ViewProgress')}>View Progress</button>
      </div>

      <div className="content">
        {activeComponent === 'CreatePlan' && <CreatePlan />}
        {activeComponent === 'InviteTrainee' && <InviteTrainee />}
        {activeComponent === 'CreateGroup' && <CreateGroup/>}
        {activeComponent === 'ViewProgress' && <ViewProgress />}
      </div>
    </div>
  );
};

export default TrainerDashboard;
