// ViewGroup.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getGroupDetails } from '../../service/api';
const ViewGroup = ({ groupId }) => {
  const [group, setGroup] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchGroup = async () => {
      try {
        if (!groupId) {
          setError('Group ID is missing!');
          setLoading(false);
          return;
        }
        const res = await axios.get(`http://localhost:5000/api/group/${groupId}`);
        setGroup(res.data);
      } catch (err) {
        console.error(err);
        setError('Failed to fetch group details.');
      } finally {
        setLoading(false);
      }
    };

    fetchGroup();
  }, [groupId]);

  if (loading) return <div>Loading group details...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Group: {group.name}</h2>
      <h3>Trainees in this group:</h3>
      {group.trainees.length > 0 ? (
        <ul>
          {group.trainees.map((trainee) => (
            <li key={trainee._id}>
              {trainee.name} ({trainee.email})
            </li>
          ))}
        </ul>
      ) : (
        <p>No trainees in this group yet.</p>
      )}
    </div>
  );
};

export default ViewGroup;
