import React, { useState, useEffect } from 'react';
import axios from 'axios';

const InviteTrainee = () => {
  const [groups, setGroups] = useState([]);
  const [formData, setFormData] = useState({
    email: '',
    group: '',
  });

  useEffect(() => {
    const fetchGroups = async () => {
      const trainerId = 'trainer_object_id_here'; // replace dynamically
      const res = await axios.get(`/api/groups/${trainerId}`);
      setGroups(res.data);
    };
    fetchGroups();
  }, []);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/invites', {
        ...formData,
        trainer: 'trainer_object_id_here', // replace dynamically
      });
      alert('Invitation sent successfully!');
      setFormData({ email: '', group: '' });
    } catch (error) {
      alert(error.response?.data?.message || 'Failed to send invite');
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Invite Trainee</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="email"
          name="email"
          placeholder="Trainee's Email"
          value={formData.email}
          onChange={handleChange}
          required
          className="w-full border p-2 rounded"
        />
        <select
          name="group"
          value={formData.group}
          onChange={handleChange}
          required
          className="w-full border p-2 rounded"
        >
          <option value="">Select Group</option>
          {groups.map(group => (
            <option key={group._id} value={group._id}>{group.name}</option>
          ))}
        </select>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Send Invite
        </button>
      </form>
    </div>
  );
};

export default InviteTrainee;
