// TraineePlan.js
import React, { useState, useEffect } from 'react';
import { getAssignedPlan } from '../../service/api'; // API service to get the plan

const TraineePlan1 = () => {
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(true);
  const traineeId = localStorage.getItem('traineeId'); // Get trainee ID from local storage
  if (!traineeId) {
    console.error('Trainee ID is missing');
    return; // Don't proceed if traineeId is missing
  }
  useEffect(() => {
    const fetchPlan = async () => {
      try {
        const response = await getAssignedPlan(traineeId);
        setPlan(response.data);  // Assuming the response contains the plan data
        setLoading(false);
      } catch (error) {
        console.error('Error fetching plan:', error);
        setLoading(false);
      }
    };
    fetchPlan();
  }, [traineeId]);

  if (loading) {
    return <div>Loading your training plan...</div>;
  }

  if (!plan) {
    return <div>No plan assigned.</div>;
  }

  return (
    <div className="plan-container">
      <h2>Assigned Plan: {plan.name}</h2>
      <p><strong>Duration:</strong> {plan.duration} days</p>
      <p><strong>Type:</strong> {plan.type}</p>
      <p><strong>Workout:</strong> {plan.workout}</p>
      <p><strong>Status:</strong> {plan.status}</p>
    </div>
  );
};

export default TraineePlan1;
