import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api',
});

export const signup = async (formData) => {
  const response = await api.post('/users/signup', formData);
  return response.data;
};

export const googleSignup = async (tokenId) => {
  const response = await api.post('/google-login', { tokenId });
  return response.data;
};

// Get groups API
export const getGroups = async () => {
  const response = await axios.get(`${API_URL}/groups`, {
    headers: {
      'Authorization': `Bearer ${localStorage.getItem('token')}`,  // Assuming you have token-based authentication
    },
  });
  return response;
};

// Send invite API
export const inviteTrainee = async (inviteData) => {
  const response = await axios.post(`${API_URL}/invites`, inviteData, {
    headers: {
      'Authorization': `Bearer ${localStorage.getItem('token')}`,
    },
  });
  return response;
};

export default api;
