import React, { useState } from 'react';
import axios from 'axios';

const UpdateActivity = ({ traineeId }) => {
  const [formData, setFormData] = useState({
    date: '',
    activityType: '',
    duration: '',
    city: '',
    country: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/activity/update', {
        ...formData,
        traineeId,
      });
      alert('Activity updated successfully!');
    } catch (error) {
      console.error('Error updating activity:', error);
      alert('Failed to update activity.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 shadow rounded">
      <h2 className="text-xl font-bold mb-4">Update Activity</h2>

      <input type="date" name="date" value={formData.date} onChange={handleChange} required className="w-full p-2 mb-2 border rounded" />

      <input type="text" name="activityType" placeholder="Exercise Type" value={formData.activityType} onChange={handleChange} required className="w-full p-2 mb-2 border rounded" />

      <input type="number" name="duration" placeholder="Duration (in minutes)" value={formData.duration} onChange={handleChange} required className="w-full p-2 mb-2 border rounded" />

      <input type="text" name="city" placeholder="City" value={formData.city} onChange={handleChange} required className="w-full p-2 mb-2 border rounded" />

      <input type="text" name="country" placeholder="Country" value={formData.country} onChange={handleChange} required className="w-full p-2 mb-4 border rounded" />

      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Submit</button>
    </form>
  );
};

export default UpdateActivity;
