import React, { useState, useEffect } from 'react';
import { getAssignedPlan } from '../../service/api';

const TraineePlan = () => {
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPlan = async () => {
      const traineeId = localStorage.getItem('traineeId');
      localStorage.setItem('traineeId', 'some-trainee-id');

      if (!traineeId) {
        console.error('Trainee ID is missing');
        setLoading(false);
        return;
      }

      try {
        const response = await getAssignedPlan(traineeId);
        setPlan(response.data);
      } catch (error) {
        console.error('Error fetching plan:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPlan();
  }, []); // Empty dependency array means this effect runs once when the component is mounted

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!plan) {
    return <div>No plan assigned.</div>;
  }

  return (
    <div>
      <h2>Assigned Plan</h2>
      <p>Plan Name: {plan.name}</p>
      <p>Duration: {plan.duration}</p>
      <p>Type: {plan.type}</p>
      <p>Workout: {plan.workout}</p>
    </div>
  );
};

export default TraineePlan;
