import React, { useState } from 'react';
import axios from 'axios';
import '../../styles/InviteTrainee.css';
const InviteTrainee = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleInvite = async (e) => {
    e.preventDefault();
    
  if (!email) {
    setMessage('Email is required');
    return;
  }
    try {
      const response = await axios.post('http://localhost:5000/api/invite', { email },{
        headers:{
          'Content-Type':'application/json',
        }
      });
      setMessage(response.data.message);
      console.log(response.data ,'invite sent successfully')
    } catch (error) {
      setMessage('Failed to send invite');
    }
  };

  return (
    <div className="invite-container">
      <h2>Invite Trainee</h2>
      <form onSubmit={handleInvite}>
        <label htmlFor="email">Trainee Email:</label>
        <input
          type="email"
          name="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Send Invite</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default InviteTrainee;
